import type { ChartConfig, SupportedChartSeries } from "@arcgis/charts-shared-utils";
/**
 * Payload for model config change event.
 */
export type ModelConfigChangePayload<T extends SupportedChartSeries = SupportedChartSeries> = {
    /**
     * Name of the function that was called which triggered the change.
     */
    functionCalled: string;
    /**
     * New chart config.
     */
    config: ChartConfig<T>;
};
